import React, { Component } from "react";
import "./index.css";
import todosList from "./todos.json";


class App extends Component {
  state = {
    todos: todosList,
    value:""
  };
  handleDelete = ( event,todoIdToDelete) => {
    const newTodoList = this.state.todos.filter(
      todo => todo.id !== todoIdToDelete
    );
    this.setState({ todos: newTodoList });
  };

handleCreate = (event) => {
  if(event.key === "Enter"){
    const newTodoList = this.state.todos.slice();
    newTodoList.push({
      "userId": 1,
      "id": Math.floor(Math.random() * 10000000),
      "title": this.state.value,
      "completed": false
    })
    this.setState({todos: newTodoList, value: ""});
    this.setState({ })
  }
};

handleToggle = (event, todoIdToToggle) => {

   const newTodoList = this.state.todos.map(todo => {
    if(todo.id === todoIdToToggle){
      const newTodo = {...todo };
      newTodo.completed =! newTodo.completed;
      return newTodo;
    }
    return todo;
  });
  this.setState({todos: newTodoList});

};
handleChange = (event) => {
  this.setState({value: event.target.value});
}


  render() {
    return (
      <section className="todoapp">
        <header className="header">
          <h1>todos</h1>
          <input
            className="new-todo"
            placeholder="What needs to be done?"
            autofocus
            onKeyDown={this.handleCreate}
            onChange={this.handleChange}
            value={this.state.value}
          />
        </header>
        <TodoList handleDelete={this.handleDelete} todos={this.state.todos} />
        <footer className="footer">
          <span className="todo-count">
            <strong>0</strong> item(s) left
          </span>
          <button className="clear-completed">Clear completed</button>
        </footer>
      </section>
    );
  }
}

class TodoItem extends Component {
  render() {
    return (
      <li className={this.props.completed ? "completed" : ""}>
        <div className="view">
          <input
            className="toggle"
            type="checkbox"
            checked={this.props.completed}
          />
          <label>{this.props.title}</label>
          <button className="destroy" onClick = {event=> this.props.handleDelete(event, this.props.id)} />
          <button className="toggle" onClick = {event=> this.props.handleToggle(event, this.props.id)} />
        </div>
      </li>
    );
  }
}

class TodoList extends Component {
  render() {
    return (
      <section className="main">
        <ul className="todo-list">
          {this.props.todos.map(todo => (
            <TodoItem
            handleToggle={this.props.handleToggle}
            handleDelete={this.props.handleDelete}
            key={todo.id}
            id={todo.id}
            title={todo.title}
              completed={todo.completed}
            />
          ))}
        </ul>
      </section>
    );
  }
}

export default App;
